export function isImageValid(src) {
  return new Promise(function (resolve) {
    if (!src) {
      resolve(false);
      return;
    }
    var img = document.createElement('img');
    img.onerror = function () {
      return resolve(false);
    };
    img.onload = function () {
      return resolve(true);
    };
    img.src = src;
  });
}
